import React, {Component} from 'react';
import './spotlight.css';

import NavbarDefault from './components/navbar/navbar_default';

var src = ""

class Spotlight extends Component {
  constructor(props) {
    super(props);

    this.state = {src: ""};
  }

  componentDidMount() {
      try{
        src = this.props.location.propsSpotlight.src
        localStorage.setItem('src', src);
        this.setState({src: src});
      } catch(e) {
        this.setState({src: localStorage.getItem('src')});
      }
  }

  render(){
    return (
      <div className="container-fluid">
      <div className="container">
        <NavbarDefault />
      </div>
      <div className="container-fluid">
        <div className="row">
            <div className="col-sm-1"></div>
            <div className="col-sm-10">
              <div className="row h-100">
                <div className="col-sm-3"></div>
                <div className="col-sm-6 text-center">
                  <span className="helper" />
                  <img className="spotlight_photo rounded" src={require(""+localStorage.getItem('src'))} alt="spotlight_photo"/>
                </div>
                <div className="col-sm-3">
                  <div className="card photo_description">
                    <div className="card-body">
                      <h5 className="card-title">Card title</h5>
                      <h6 className="card-subtitle mb-2 text-muted">Card subtitle</h6>
                      <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="col-sm-1"></div>
        </div>
      </div>
      </div>
    )
  }
}

export default Spotlight;
