import React, {Component} from 'react'

import './about.css'
import angelo from './public/materials/angelo.png'
import NavbarDefault from './components/navbar/navbar_default'

class About extends Component{
  constructor(props) {
    super(props)
  }

  componentDidMount() {
    var request = require("request");

    var options = {
      method: 'POST',
      url: 'https://ohanotherone.eu.auth0.com/api/v2/connections',
      headers: {authorization: 'Bearer YOUR_API_V2_TOKEN_HERE'},
      body: {
        name: 'custom-goodreads',
        strategy: 'oauth1',
        enabled_clients: ['YOUR_ENABLED_CLIENT_ID'],
        options: {
          client_id: 'YOUR_GOODREADS_KEY',
          client_secret: 'YOUR_GOODREADS_SECRET',
          requestTokenURL: 'http://www.goodreads.com/oauth/request_token',
          accessTokenURL: 'http://www.goodreads.com/oauth/access_token',
          userAuthorizationURL: 'http://www.goodreads.com/oauth/authorize',
          scripts: {
            fetchUserProfile: 'function(token, tokenSecret, ctx, cb) {var OAuth = new require("oauth").OAuth; var parser = require(\'xml2json\'); var oauth = new OAuth(ctx.requestTokenURL, ctx.accessTokenURL, ctx.client_id, ctx.client_secret, "1.0", null, "HMAC-SHA1"); oauth.get("https://www.goodreads.com/api/auth_user", token, tokenSecret, function(e, xml, r) { console.log(xml); if (e) return cb(e); if (r.statusCode !== 200) return cb(new Error("StatusCode: " + r.statusCode)); try { var jsonResp = JSON.parse(parser.toJson(xml)); var user = jsonResp.GoodreadsResponse.user; cb(null, user); } catch (e) { console.log(e); cb(new UnauthorizedError("[+] fetchUserProfile: Goodreads fetch script failed. Check Webtask logs")); } });}'
          }
        }
      },
      json: true
    };

    request(options, function (error, response, body) {
      if (error) throw new Error(error);

      console.log(body);
    });
  }

  render() {
    return(
      <div className="container">
        <NavbarDefault />
        <div className="blank" />
        <div className="row">
          <div id="personal-info" className="border col-sm-4">
            <div className="row d-flex justify-content-center">
              <img src={angelo} alt="angelo" width="150px" height="150px" />
            </div>
            <div className="row info">
              <div>
                <p>Angelo Rosace</p>
                <p>31 . 12 . 1996</p>
                <p>Italian based in Barcelona</p>
                <p>Freelance Illustrator</p>
                <p>Master student in Bioinformatics for Health Science</p>
              </div>
            </div>
          </div>
          <div className="col-sm-1"></div>
          <div id="project" className="col-sm-7">
            <div className="row border">
              <h1>The project</h1>
            </div>
            <div className="blank" />
            <div className="row">
              <div className="col-sm-6 border">Spotify playlists</div>
              <div className="col-sm-6 border">b</div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default About
