import React, {Component} from 'react';
import './spotlight.css';

import NavbarDefault from './components/navbar/navbar_default';

var src = ""

class Spotlight extends Component {
  constructor(props) {
    super(props);

    this.state = {src: ""};
  }

  componentDidMount() {
      if( !this.props.location.propsSpotlight.src ) {
          this.setState({src: localStorage.getItem('src')});
      } else {
        localStorage.setItem('src',this.props.location.propsSpotlight.src);
        this.setState({src: this.props.location.propsSpotlight.src});
      }
  }

  render(){
    return (
      <div>
      <div className="container">
        <NavbarDefault />
      </div>
      <div className="container-fluid">
        <div className="row">
            <div className="col-sm-1"></div>
            <div className="col-sm-10">
              <div className="row">
                <div className="col-sm-3"></div>
                <div className="col-sm-6">
                  <h1>{localStorage.getItem('src')}</h1>
                </div>
                <div className="col-sm-3"></div>
              </div>
            </div>
            <div className="col-sm-1"></div>
        </div>
      </div>
      </div>
    )
  }
}

export default Spotlight;
