import React, {Component} from 'react';
import NavbarDefault from './components/navbar/navbar_default';
import logo from './public/logo/logo_white.svg';

import Photo from './components/photo/photo';

var images = []
const photo_src = './public/photos/';

class Portfolio extends Component {
  constructor(props) {
    super(props);

    this.state = {
      photoArray : this.updatePhotoArray(images)
    };


    this.getPhotos = this.getPhotos.bind(this);
    this.updatePhotoArray = this.updatePhotoArray.bind(this);
  }

  componentDidMount() {
    images = this.getPhotos(require.context('./public/photos', false, /\.(png|jpe?g|svg)$/));

  }

  updatePhotoArray(images) {
    var src;
    var photos_src_as_of_now = [];
    images.forEach((item, i) => {
      var name_with_ext = item.split("/")[3];
      var splitted_name = name_with_ext.split(".");
      src = photo_src + splitted_name[0] + "." + splitted_name[splitted_name.length-1];
      photos_src_as_of_now.push(src);
    });
    return photos_src_as_of_now;
  }

  getPhotos(r) {
    return r.keys().map(r);
  }

  render() {
    return(
      <div className="container">
        <NavbarDefault />
        <div className="row">
          {
            console.log(this.state.photoArray),
            this.state.photoArray.forEach((item, i) => {
              console.log(item);
              return (
                <Photo src={photo_src}/>
              );
            })
          }
        </div>
      </div>
    );
  }
}

export default Portfolio;
