import React from 'react';
import logo from './public/logo/logo_black.svg';
import './App.css';

import {useSpring, animated, config} from 'react-spring';
import VisibilitySensor from "./components/visibilitySensor";
import { Spring } from "react-spring/renderprops";

import NavbarDefault from './components/navbar/navbar_default'
import title from './public/materials/titolo.svg'
import one from './public/logo/parti_black/1.svg'
import two from './public/logo/parti_black/2.svg'
import three from './public/logo/parti_black/3.svg'
import four from './public/logo/parti_black/4.svg'
import five from './public/logo/parti_black/5.svg'
import six from './public/logo/parti_black/6.svg'

function App() {
  const fade = useSpring({
    config: {
      duration: 1500
    },
    from: {
      opacity: 0
    },
    to: {
      opacity: 1
    }
  });

  const width = 0

  return (
    <div id="first" className="container-fluid">
      <div id="first" className="container">
        <NavbarDefault />
        <div className="title row">
          <animated.img src={title} alt="title" width="100%" style={fade}/>
        </div>
      </div>
      <div id="second" className="container-fluid">
        <div id="second" className="row">
          <VisibilitySensor>
            {({ isVisible }) => (
              <Spring delay={300} to={{ opacity: isVisible ? 1 : 0 }}>
                {({ opacity }) => <div style={{ opacity }} className="idea"><span>The Idea</span></div>}
              </Spring>
            )}
          </VisibilitySensor>
        </div>
        <div id="second" className="blank row" />
        <div id="second" className="row">
          <div className="a d-flex justify-content-center">
          {this.width=window.innerWidth}
          <VisibilitySensor>
          {({ isVisible }) => (
            <Spring delay={600} from={{opacity: 1}} to={{opacity: isVisible ? 0 : 1}}>
                {({ opacity}) => <img style={{opacity}} src={logo} alt="logo" height="300"/>}
            </Spring>
          )}
          </VisibilitySensor>
          </div>
          <VisibilitySensor>
          {({ isVisible }) => (
            <Spring delay={1000} from={{x: '0px'}} to={{opacity: isVisible ? 1 : 0, x:'1000px'}}>
                {({ opacity, x }) => <img style={{opacity, transform:'translate3d('+x+',0,0)'}} className="tmp" src={five} alt="logo" height="300"/>}
            </Spring>
          )}
          </VisibilitySensor>
          <VisibilitySensor>
          {({ isVisible }) => (
            <Spring delay={1100} from={{x: '0px'}} to={{opacity: isVisible ? 1 : 0, x:'800px'}}>
                {({ opacity, x }) => <img style={{opacity, transform:'translate3d('+x+',0,0)'}} className="tmp" src={four} alt="logo" height="300"/>}
            </Spring>
          )}
          </VisibilitySensor>
          <VisibilitySensor>
          {({ isVisible }) => (
            <Spring delay={1200} from={{x: '0px'}} to={{opacity: isVisible ? 1 : 0, x:'600px'}}>
                {({ opacity, x }) => <img style={{opacity, transform:'translate3d('+x+',0,0)'}} className="tmp" src={three} alt="logo" height="300"/>}
            </Spring>
          )}
          </VisibilitySensor>
          <VisibilitySensor>
          {({ isVisible }) => (
            <Spring delay={1300} from={{x: '0px'}} to={{opacity: isVisible ? 1 : 0, x:'400px'}}>
                {({ opacity, x }) => <img style={{opacity, transform:'translate3d('+x+',0,0)'}} className="tmp" src={two} alt="logo" height="300"/>}
            </Spring>
          )}
          </VisibilitySensor>
          <VisibilitySensor>
          {({ isVisible }) => (
            <Spring delay={1400} from={{x: '0px'}} to={{opacity: isVisible ? 1 : 0, x:'200px'}}>
                {({ opacity, x }) => <img style={{opacity, transform:'translate3d('+x+',0,0)'}} className="tmp" src={one} alt="logo" height="300"/>}
            </Spring>
          )}
          </VisibilitySensor>
        </div>
      </div>
    </div>
  );
}

export default App;
