import React, {Component} from 'react';
import './photo.css';

class Photo extends Component{
  constructor(props) {
    super(props);

    this.setScale = this.setScale.bind(this);
  }

  setScale(){
    var scales = ["img25","img30","img45","img50"];
    console.log(Math.random()*scales.length);
    return scales[Math.random()*scales.length]
  }

  render(){
    return(
      <img src={this.props.src} alt="photo" className={"rounded img-fluid"+this.setScale()}/>
    );
  }
}

export default Photo;
